package RelationalOperator;

public class Relational_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int k=20;
		int l=15;
		int p =20;
		
		System.out.println(k>l);
		System.out.println(k<l); 
		System.out.println(k>=l);//k>l or k=l true or false
		System.out.println(k>=p);//k>p or k=p  false or true
		System.out.println(k<=l); //k<l or k=l false or false
		System.out.println(k<=p);//k<p or k=p false or true
		System.out.println(k!=l);//k is not equal to l? true 
		System.out.println(k!=p); // k is not equal to p? false

	}

}
